// import React, {useEffect, useRef} from 'react';
import React from 'react';
// css
import './App.css'
// component enfant
import About from './About.js'
import Logo from './Logo.js'
import Menu from './menu.js'
// import librairie
import {TweenMax,TimelineMax, EasePack} from 'gsap';
// import assets
import img_lr from './img/lr.png'
import { tsConstructorType } from '@babel/types';

// React app
class App extends React.Component{
  // animation menu 
  constructor(props){
    super(props)

      this.test = () => {
        var toto = true
        var tl = new TimelineMax();
        var menu = '.wrapper__menu'

    if(document.getElementsByClassName('wrapper++menu').style.height === "0%"){

      tl.set(menu,{
        height: '100vh'
      })
      .to('.left',0.5,{
        height: '100vh',
        ease: "Expo.easeInOut"
      },"test")
      .to('.right',0.5,{
        height: '100vh',
        ease: "Expo.easeInOut"
      },"test+=0.1")
      .staggerTo('.button__burger .trait',0.1,{
        backgroundColor: 'white'
      })
      .staggerTo('.menu ul li', 0.5, {
        y:0,
        opacity: 1,
        ease: "Expo.easeInOut"
      },0.1,"test+=0.2")
        toto = !toto
    } 
    else {
        tl.to('.right',0.2,{
          height: 0,
          ease: "Expo.easeInOut"
        })
        .to('.left',0.2,{
          height: 0,
          ease: "Expo.easeInOut"
        },"+=0.1")
        .set(menu,{
          height: 0
        })
        toto = !toto
    }
    console.log(toto)

      }

  }
  // render
  render(){


    return(
        <div className="wrapper__all">
          <Menu />
          <div onClick={this.test} className="button__burger">
            <div className="trait"></div>
            <div className="trait"></div>
            <div className="trait"></div>
          </div>
             <div className="wrapper__home">
             <Logo />
             <img className="logo__lr"src={img_lr} alt=""/>
              <p>Developpeur front end <b>cr√©atif</b> junior</p>
             </div>
             <About />
             {/* fin du conponent */}
        </div>
    )
  }
}

export default App;
