// import React, {useEffect, useRef} from 'react';
import React from 'react';
// css
import './App.css'
// component enfant
import About from './About.js'
import Logo from './Logo.js'
import Menu from './menu.js'
// import librairie
import {TweenMax,TimelineMax} from 'gsap';
// import assets
import img_lr from './img/lr.png'

// React app
class App extends React.Component{
  // animation menu 
  animationMenu = () => {
    var toto = true
    var tl = new TimelineMax();
    var menu = '.wrapper__menu'

if(toto === true){

  tl.set(menu,{
    height: '100vh'
  })
  .to('.left',0.5,{
    height: '100vh',
    ease: "Expo.easeInOut"
  },"test")
  .to('.right',0.5,{
    height: '100vh',
    ease: "Expo.easeInOut"
  },"test+=0.1")
  .staggerTo('.button__burger .trait',0.1,{
    backgroundColor: 'white'
  })
  .staggerTo('.menu ul li', 0.5, {
    y:0,
    opacity: 1,
    ease: "Expo.easeInOut"
  },0.1,"test+=0.2")
   setTimeout(toto = !toto, 900)
} 
else if(toto === false){
  tl.staggerTo('.menu ul li', 0.3, {
    y: '-100px',
    opacity: 0,
    ease: "Expo.easeInOut"
  },0.1 )
  .to('.right',0.2,{
    height: 0,
    ease: "Expo.easeInOut"
  })
  .to('.left',0.2,{
    height: 0,
    ease: "Expo.easeInOut"
  },"+=0.1")
  .set(menu,{
    height: 0
  })
  setTimeout(toto = !toto, 900)
  console.log('else')
}
console.log(toto)

  }

  // component did mount

  componentDidMount(){
    var tl = new TimelineMax();

    setTimeout( () => { 
      tl.set('.logo__lr, .home__text p',{
        y:"20%"
      })
      .staggerTo('.home__img .black, .home__text .black ', 0.3,{
        width: '100%',
        ease: "Expo.easeInOut"
      },0.1,"start")

      .staggerTo('.home__img .black, .home__text .black ', 0.3,{
        left: "100%",
        ease: "Expo.easeInOut"
      },0.1,"go")

      .staggerTo('.logo__lr, .home__text p', 0.5,{
        opacity: 1,
        y:"0%",
        ease: "Expo.easeInOut"
      },"go+=0.2")



      // fin du set time
     }, 500);
  }
  // render
  render(){
    return(
        <div className="wrapper__all">
          <Menu />
          <div onClick={this.animationMenu} className="button__burger">
            <div className="trait"></div>
            <div className="trait"></div>
            <div className="trait"></div>
          </div>
             <div className="wrapper__home">
             <Logo />
              <div className="home__img">
                <div className="black"></div>
              <img className="logo__lr"src={img_lr} alt=""/>
              </div>
              <div className="home__text">
                <div className="black"></div>
              <p>Developpeur front end <b>créatif</b> junior</p>
              </div>
             </div>
             <About />
             {/* fin du conponent */}
        </div>
    )
  }
}

export default App;
